            + " (SELECT  SUM(GIATIEN)   FROM TONKHO WHERE TONKHO.MANHAPXUAT='X' AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY ='" + dto.NGAYBDKHO + "') AS THANHTIENXUAT,(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE -SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TN' AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY ='" + dto.NGAYBDKHO + "') AS TRANHAP,"
            + " (SELECT  SUM(GIATIEN)   FROM TONKHO WHERE TONKHO.MANHAPXUAT='TN' AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY ='" + dto.NGAYBDKHO + "') AS THANHTIENTRANHAP,(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TX' AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY ='" + dto.NGAYBDKHO + "') AS TRAXUAT,"
            + " (SELECT  SUM(GIATIEN)   FROM TONKHO WHERE TONKHO.MANHAPXUAT='TX' AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY ='" + dto.NGAYBDKHO + "') AS THANHTIENTRAXUAT,KHOHANG.TONKHO AS TONCUOI FROM KHOHANG) AS T1, MATHANG,NHACUNGCAP, DONVITINH WHERE MATHANG.TINHTRANG='True' AND MATHANG.MAMH=T1.MAMH AND NHACUNGCAP.MANCC=MATHANG.MANCC AND MATHANG.MADVT = DONVITINH.MADVT AND MATHANG.MAKHO='" + PublicVariable.MAKHO + "' " + SQL1 + TYPEEND;

            return getdata(SQL);
        }

