                SQL = TYPE + " SELECT TENMH+' - ' + QUYCACH AS TENMH,MATHANG.MAMH,MATHANG.GIAMUA, MATHANG.MANCC, TENNCC,KLDVT, DONVITINH,convert(varchar,NGAY,103) AS NGAY,TONTT, TONTT-TONCUOI AS CHENHLECH, TONDAU, TONDAU*KLDVT AS KLTONDAU, TONDAU*T1.GIAMUA AS THANHTIENTONDAU, NHAP, NHAP*KLDVT AS KLNHAP, THANHTIENNHAP, XUAT, XUAT*KLDVT AS KLXUAT, THANHTIENXUAT, TRANHAP, TRANHAP*KLDVT AS KLTRANHAP, THANHTIENTRANHAP, TRAXUAT, TRAXUAT*KLDVT AS KLTRAXUAT, THANHTIENTRAXUAT, TONCUOI, TONCUOI*KLDVT AS KLTONCUOI, TONCUOI*T1.GIAMUA AS THANHTIENTONCUOI FROM"
                + " (SELECT KHOHANG.MAMH,KHOHANG.GIAMUA,TONTT,NGAY,"
                + " (SELECT CASE WHEN SUM(TONKHO) IS NULL THEN 0 ELSE SUM(TONKHO) END FROM TONDAUKHOHANG WHERE TONDAUKHOHANG.MAMH=KHOHANG.MAMH  AND TONDAUKHOHANG.LOHANG=KHOHANG.LOHANG)+(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE SUM(NHAPXUAT) END FROM TONKHO WHERE TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY < '" + dto.NGAYBDKHO + "' ) AS TONDAU, "
                + " (SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='N' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY = '" + dto.NGAYBDKHO + "') AS NHAP,"
                + " (SELECT CASE WHEN SUM(GIATIEN) IS NULL THEN 0 ELSE SUM(GIATIEN) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='N' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY = '" + dto.NGAYBDKHO + "' ) AS THANHTIENNHAP,(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE -SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='X' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY  = '" + dto.NGAYBDKHO + "') AS XUAT,"
                + " (SELECT CASE WHEN SUM(GIATIEN) IS NULL THEN 0 ELSE SUM(GIATIEN) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='X' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY = '" + dto.NGAYBDKHO + "' ) AS THANHTIENXUAT,(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE -SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TN' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY  = '" + dto.NGAYBDKHO + "') AS TRANHAP,"
                + " (SELECT CASE WHEN SUM(GIATIEN) IS NULL THEN 0 ELSE SUM(GIATIEN) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TN' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY = '" + dto.NGAYBDKHO + "' ) AS THANHTIENTRANHAP,(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE SUM(NHAPXUAT) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TX' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY  = '" + dto.NGAYBDKHO + "') AS TRAXUAT,"
                + " (SELECT CASE WHEN SUM(GIATIEN) IS NULL THEN 0 ELSE SUM(GIATIEN) END  FROM TONKHO WHERE TONKHO.MANHAPXUAT='TX' AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.NGAY = '" + dto.NGAYBDKHO + "' ) AS THANHTIENTRAXUAT,(SELECT CASE WHEN SUM(TONKHO) IS NULL THEN 0 ELSE SUM(TONKHO) END FROM TONDAUKHOHANG WHERE TONDAUKHOHANG.MAMH=KHOHANG.MAMH   AND TONDAUKHOHANG.LOHANG=KHOHANG.LOHANG )+(SELECT CASE WHEN SUM(NHAPXUAT) IS NULL THEN 0 ELSE SUM(NHAPXUAT) END FROM TONKHO WHERE TONKHO.MAMH=KHOHANG.MAMH AND TONKHO.LOHANG=KHOHANG.LOHANG AND TONKHO.NGAY <= '" + dto.NGAYBDKHO + "' ) AS TONCUOI"
                + " FROM (SELECT KHOHANG.MAMH,TONKHO,GIAMUA,TONKHONGAY AS TONTT,LOHANG,TONKHOTT.NGAY FROM KHOHANG,TONKHOTT WHERE KHOHANG.MAMH=TONKHOTT.MAMH AND TONKHOTT.MAKHO='" + PublicVariable.MAKHO + "' AND TONKHOTT.NGAY='" + dto.NGAYBDKHO + "') AS KHOHANG ) AS T1, MATHANG,NHACUNGCAP, DONVITINH WHERE MATHANG.TINHTRANG='True' AND MATHANG.MAMH=T1.MAMH AND NHACUNGCAP.MANCC=MATHANG.MANCC AND MATHANG.MADVT = DONVITINH.MADVT AND MATHANG.MAKHO='" + PublicVariable.MAKHO + "' " + SQL1 + TYPEEND;
                */
                SQL = TYPE + "SELECT TENMH+' - ' + QUYCACH AS TENMH,MATHANG.MAMH,GIAMUA,MATHANG.MANCC,TENNCC,KLDVT,DONVITINH,convert(varchar,TONKHOTT.NGAY,103) AS NGAY ,TONKHONGAY as TONTT, TONKHONGAY-TONCUOI AS CHENHLECH,  TONDAU, TONDAU*KLDVT AS KLTONDAU,TONDAU*GIAMUA AS THANHTIENTONDAU,  NHAP, NHAP*KLDVT AS KLNHAP,GIANHAP AS THANHTIENNHAP,  XUAT,XUAT*KLDVT KLXUAT,GIAXUAT AS THANHTIENXUAT, TRANHAP,TRANHAP*KLDVT AS KLTRANHAP, GIATRANHAP AS THANHTIENTRANHAP,  TRAXUAT, TRAXUAT*KLDVT AS KLTRAXUAT, GIATRAXUAT AS THANHTIENTRAXUAT,TONCUOI, TONCUOI*KLDVT AS KLTONCUOI, TONCUOI*GIAMUA AS THANHTIENTONCUOI FROM( "
                + " SELECT TONDAU,TONDAU+ISNULL(NHAP,0)+ISNULL(XUAT,0)+ISNULL(TRANHAP,0)+ISNULL(TRAXUAT,0) AS TONCUOI,ISNULL(NHAP,0)AS NHAP,ISNULL(GIANHAP,0)AS GIANHAP,-ISNULL(XUAT,0)AS XUAT,ISNULL(GIAXUAT,0)AS GIAXUAT,-ISNULL(TRANHAP,0)AS TRANHAP,ISNULL(GIATRANHAP,0)AS GIATRANHAP,ISNULL(TRAXUAT,0)AS TRAXUAT,ISNULL(GIATRAXUAT,0)AS GIATRAXUAT,T1.MAMH FROM "
                + " (SELECT T11.MAMH, ISNULL(NHAPXUAT,0)+TONKHO AS TONDAU FROM (SELECT SUM(NHAPXUAT)AS NHAPXUAT,MATHANG.MAMH FROM TONKHO,MATHANG WHERE TONKHO.MAMH=MATHANG.MAMH AND TONKHO.NGAY < '" + dto.NGAYBDKHO + "' AND TONKHO.MAKHO='" + PublicVariable.MAKHO + "'  GROUP BY MATHANG.MAMH) AS T11, (SELECT SUM(TONKHO) AS TONKHO,MATHANG.MAMH  FROM TONDAUKHOHANG,MATHANG WHERE TONDAUKHOHANG.MAMH=MATHANG.MAMH AND MATHANG.MAKHO='" + PublicVariable.MAKHO + "' GROUP BY MATHANG.MAMH) AS T22 WHERE T11.MAMH=T22.MAMH)  AS T1"
                + " FULL OUTER JOIN"
                + " (SELECT MAMH,sum(CASE WHEN MANHAPXUAT='N' THEN NHAPXUAT ELSE 0 end) As NHAP,sum(CASE WHEN MANHAPXUAT='N' THEN GIATIEN ELSE 0 end) As GIANHAP,sum(CASE WHEN MANHAPXUAT='X' THEN NHAPXUAT ELSE 0 end) As XUAT,sum(CASE WHEN MANHAPXUAT='X' THEN GIATIEN ELSE 0 end) As GIAXUAT,sum(CASE WHEN MANHAPXUAT='TN' THEN NHAPXUAT ELSE 0 end) As TRANHAP,sum(CASE WHEN MANHAPXUAT='TN' THEN GIATIEN ELSE 0 end) As GIATRANHAP,sum(CASE WHEN MANHAPXUAT='TX' THEN NHAPXUAT ELSE 0 end) As TRAXUAT,sum(CASE WHEN MANHAPXUAT='TX' THEN GIATIEN ELSE 0 end) As GIATRAXUAT"
                + " FROM TONKHO WHERE TONKHO.NGAY='" + dto.NGAYBDKHO + "' AND TONKHO.MAKHO='" + PublicVariable.MAKHO + "' GROUP BY MAMH)AS T2 ON T1.MAMH=T2.MAMH) AS T3,(SELECT MAMH,NGAY,TONKHONGAY FROM TONKHOTT WHERE TONKHOTT.MAKHO='" + PublicVariable.MAKHO + "' AND TONKHOTT.NGAY='" + dto.NGAYBDKHO + "') AS TONKHOTT,MATHANG,NHACUNGCAP,DONVITINH "
                + " WHERE T3.MAMH=MATHANG.MAMH AND MATHANG.MANCC=NHACUNGCAP.MANCC AND MATHANG.MAMH=TONKHOTT.MAMH AND MATHANG.MADVT=DONVITINH.MADVT AND MATHANG.MAKHO='" + PublicVariable.MAKHO + "' " + SQL1 + TYPEEND2;
            }
            return getdata(SQL);
        }

