        public DataTable getDoanhThu(clDTO dto)
        {
            String SQL ="select  NGAYXUAT , TENKH as 'Tên Khách hàng',TENKV ,SUM(TIENPHAITRA) AS  'TongDoanhThu' FROM HOADONXUAT,KHACHHANG,KHUVUC WHERE KHUVUC.MAKV=KHACHHANG.MAKV AND KHACHHANG.MAKH=HOADONXUAT.MAKH  AND  NGAYXUAT BETWEEN '"+dto.NGAYBD+"' AND '"+dto.NGAYKT+"' group by TENKH,TENKV,ngayxuat AND HOADONXUAT.MAKHO='"+PublicVariable.MAKHO+"'";
            
            return getdata(SQL);
        }
        public DataTable getcpmuahangngay(clDTO dto)
        {
            string SQL = "SELECT NGAYNHAP, SUM(NHAP) AS NHAP, SUM(TRAXUAT) AS TRAXUAT FROM (SELECT NGAYNHAP, TIENPHAITRA AS NHAP, 0 AS TRAXUAT FROM HOADONNHAP WHERE MAKHO='" + PublicVariable.MAKHO + "' AND TYPE=1 AND NGAYNHAP BETWEEN '" + dto.NGAYBD + "' AND '" + dto.NGAYKT + "' UNION ALL SELECT NGAYXUAT AS NGAYNHAP, 0 AS NHAP, TIENPHAITRA AS TRAXUAT FROM TRAHOADONXUAT WHERE MAKHO='" + PublicVariable.MAKHO + "'  AND TYPE=1 AND NGAYXUAT BETWEEN '" + dto.NGAYBD + "' AND '" + dto.NGAYKT + "' ) AS T2 GROUP BY NGAYNHAP";
            return getdata(SQL);
        }

